mapper.insert:

	SELECT NVL(MAX(employee_id),0) + 1
	FROM employees							   

INSERT INTO employees
		(	
			employee_id
			, first_name
			, last_name
			, email
			, hire_date
			, job_id

			, salary

		)
	   VALUES
	   (
	   		#{employeeId}
	   		,#{firstName}
	   		,#{lastName}
	   		,#{email}


	   				,#{hireDate}


	   				,sysdate


	   		,#{jobId}

	   		,#{salary}

	   )

mapper.update:
UPDATE employees
   SET
   	  first_name = #{firstName}
   	  , email    = #{email}
   	  , salary   = #{salary}
WHERE employee_id = #{employeeId}
